package Decision_Making;

public class If_Statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// IF Statement --don't use ; & if statement wrong then console can't show

		int a = 45, b = 34;
		if (a < b) {
			System.out.println("a is smaller");
		}

		if (a > b) {
			System.out.println("a is smaller");
		}
		
		int x = 34, y = 34;
		if (x != y) {
			System.out.println("x and y are equal");
		}
		
		int c = 30, z = 34;
		if (c != z) {
			System.out.println("c and z are equal");
		}
		int p = 45, q = 45;
		if (p == q) {
			System.out.println("p and q are equal");
		}
		int u = 40, i = 45;
		if (u == i) {
			System.out.println("u and i are equal");
		}
	}

}
