package Operator;

public class Bitwise_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//  &  AND Operator
	int a=12, b=34,c;
	c=a & b;
	System.out.println("a & b :"+c);
	
	
	//  | OR Operator
	c=a |b;
	System.out.println("a | b :"+c);
	
	//  ^  XOR Operator
	c=a ^ b;
	System.out.println("a ^ b :"+c);
			
		
	}

}
