package Operator;

public class Rational_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		// Less Than
		int a=12, b=13;
		boolean x;
		
		x=a<b;
		System.out.println("a<b :"+x);
		
		//Greater Than
		x=a>b;
		System.out.println("a>b :"+x);
		
		//Less than equal to
		x=a<=b;
		System.out.println("a<=b :"+x);
		
		//Greater than equal to
		x=a>=b;
		System.out.println("a>=b :"+x);
		
		//equal to equal to
		x=a==b;
		System.out.println("a==b :"+x);
		
		//Not equal to
		x=a!=b;
		System.out.println("a!=b :"+x);




		
		
		
		
	}

}
